<link rel="stylesheet" type="text/css" href="slide/css/base/advanced-slider-base.css" media="screen"/><link rel="stylesheet" type="text/css" href="slide/css/glossy-square/gray/glossy-square-gray.css" media="screen"/><script type="text/javascript" src="slide/js/jquery-1.9.1.min.js"></script><script type="text/javascript" src="slide/js/jquery.touchSwipe.min.js"></script><script type="text/javascript" src="slide/js/jquery.advancedSlider.min.js"></script><script type="text/javascript">	jQuery(document).ready(function($){		$('#responsive-slider').advancedSlider({width: 1150,												height: 570,												responsive: true,												skin: 'glossy-square-gray',												effectType: 'swipe',												slideshow: true,												pauseSlideshowOnHover: true,												slideButtons: false,												thumbnailType: 'scroller',												thumbnailWidth: 282,												thumbnailHeight: 190,		});	});</script><body>	<div class="advanced-slider" id="responsive-slider" style="margin-top: -20px;">		<ul class="slides">        <?php                $sql_noticia_princial = "SELECT  *                                        FROM(                                        SELECT noticia_id, titulo, img, posicao_img, nome, bg, sub_editora, destaque_banner FROM                                        noticia INNER JOIN                                        sub_editoriais ON noticia.sub_editoriais_id = sub_editoriais.sub_editoriais_id                                        INNER JOIN editoria ON editoria.editoria_id = sub_editoriais.editoria_id                                        WHERE destaque_banner = 1 AND publicar = 1                                        ORDER BY noticia_id DESC LIMIT 0,4) q                                        ORDER BY rand()";                        $executa_sql_noticia_princial = mysql_query($sql_noticia_princial)or die(mysql_error());                                while ($dados_executa_princial = mysql_fetch_array($executa_sql_noticia_princial)) {                    $imagem = $dados_executa_princial['img'];                    $posicao = $dados_executa_princial['posicao'];        ?>			<li class="slide">				<a href="materias.php?idt=<?php echo $dados_executa_princial['noticia_id']; ?>"><img class="image" src="admin/imagens/noticia/<?php echo $imagem ?>" style="border-radius: none;" alt="<?php echo $dados_executa_princial['titulo']; ?>"/></a>    			<img class="thumbnail" style="float: left;"    			     src="admin/imagens/noticia/<?php echo $imagem ?>"     			     alt="<?php echo $dados_executa_princial['titulo']; ?>"/>                                <div class="layer <?php echo $dados_executa_princial['bg']; ?>" data-position="bottomLeft" data-horizontal="30" data-vertical="95" data-transition="left" data-delay="500">                	<?php echo $dados_executa_princial['sub_editora']; ?>                </div>                                <div class="layer titulo-slide" data-position="bottomLeft" data-horizontal="30" data-vertical="30" data-width="520" data-transition="down">					<?php echo $dados_executa_princial['titulo']; ?>				</div>							</li>			<?php			}			?>											</ul>	</div>	</body>