<div class="ot-slider">                    <!-- BEGIN .ot-slide -->                    <div class="ot-slide">                        <?php                        $seleciona_noticia = "SELECT noticia_id, titulo, img, posicao_img, nome, bg, sub_editora, destaque_banner FROM noticia INNER JOIN                                                  sub_editoriais ON noticia.sub_editoriais_id = sub_editoriais.sub_editoriais_id                                                  INNER JOIN editoria ON editoria.editoria_id = sub_editoriais.editoria_id                                                  WHERE destaque_banner = 1                                                                                                    ORDER BY noticia_id DESC LIMIT 0,3";                        $executa_seleciona_noticia = mysql_query($seleciona_noticia)or die(mysql_error());                        $cont3 = 1;                        $cont = 1;                        $cont2 = 1;                        $cont4 = 1;                        while ($array_noticiap = mysql_fetch_array($executa_seleciona_noticia)) {                            ?>                            <div class="ot-slider-layer <?php                            if ($cont2++ == 1) {                                echo "first";                            } else {                                echo "second";                            }                            if ($cont3++ == 1) {                                echo " borda-destaque";                            }                            if ($cont4++ == 2) {                                echo " borda-destaque";                            }                            ?>">                                <a href="materias.php?idt=<?php echo $array_noticiap['noticia_id']; ?>" style="background-image:url(admin/imagens/noticia/<?php echo $array_noticiap['img'] ?>); background-position: <?php                                if ($array_noticiap['posicao_img'] == 1) {                                    echo "left";                                } else if ($array_noticiap['posicao_img'] == 2) {                                    echo "center";                                } else if ($array_noticiap['posicao_img'] == 3) {                                    echo "right";                                }                                ?> top;">                                    <span class="content-bottom">                                        <span class="categories">                                            <span style="font-weight: 600;" class="category-tag <?php echo $array_noticiap['bg']; ?>"><?php echo $array_noticiap['sub_editora']; ?></span>                                        </span>                                        <strong style="font-weight: 600;"><?php echo $array_noticiap['titulo']; ?></strong>                                    </span>                                    <img src="images/<?php                                    if ($cont++ == 1) {                                        echo "transparent-horz-627.png";                                    } else {                                        echo "transparent-vert-297.png";                                    }                                    ?>" alt="" />                                </a>                            </div>                            <?php                        }                        ?>                        <!-- END .ot-slide -->                    </div>                    <!-- END .ot-slider -->                </div>