<div id='carrega' class="ot-slider" >    <!-- BEGIN .ot-slide -->    <div class="ot-slide" id="destaque">        <?php        $sql_noticia_princial = "SELECT  *                                FROM(                                SELECT noticia_id, titulo, img, posicao_img, nome, bg, sub_editora, destaque_banner FROM                                noticia INNER JOIN                                sub_editoriais ON noticia.sub_editoriais_id = sub_editoriais.sub_editoriais_id                                INNER JOIN editoria ON editoria.editoria_id = sub_editoriais.editoria_id                                WHERE destaque_banner = 1 AND publicar = 1                                ORDER BY noticia_id DESC LIMIT 0,5) q                                ORDER BY rand()";        $executa_sql_noticia_princial = mysql_query($sql_noticia_princial)or die(mysql_error());        $contfirst = 1;        while ($dados_executa_princial = mysql_fetch_array($executa_sql_noticia_princial)) {            $imagem = $dados_executa_princial['img'];            $posicao = $dados_executa_princial['posicao'];            ?>            <div class="ot-slider-layer <?php            $varcontfirs = $contfirst++;            if ($varcontfirs == 1) {                echo "first";            } else {                echo "second";            }            ?>"                 <?php                 if ($varcontfirs == 1 || $varcontfirs == 2 || $varcontfirs == 4) {                     ?>                     style="border-right: 3px solid #fff;"                     <?php                 }                 ?>                 >                <a href="materias.php?idt=<?php echo $dados_executa_princial['noticia_id']; ?>" <?php                if ($varcontfirs == 1) {                    ?>                       style='background-image:url(admin/imagens/noticia/<?php echo $imagem ?>); background-position:  <?php echo $posicao ?> top;'                       <?php                   }                   ?> <?php                   if ($varcontfirs <> 1) {                       ?>                       class="p-b"                       <?php                   }                   ?> >                    <span class="content-bottom">                        <span class="categories">                            <span class="category-tag <?php echo $dados_executa_princial['bg']; ?>"><?php echo $dados_executa_princial['sub_editora']; ?></span>                        </span>                        <?php if ($varcontfirs == 1) {                            ?>                            <strong><?php echo $dados_executa_princial['titulo']; ?></strong>                        <?php } else {                            ?>                            <p style="font-weight: 600;"><?php echo $dados_executa_princial['titulo']; ?></p>                            <?php                        }                        ?>                    </span>                    <?php                    if ($varcontfirs == 1) {                        ?>                        <img src="images/transparent-horz-627.png" alt="" />                        <?php                    } else {                        ?>                        <img class="preto_branco"  src="admin/imagens/noticia/<?php echo $dados_executa_princial['img']; ?>" width="297" alt="" />                        <?php                    }                    ?>                </a>            </div>            <?php        }        ?>        <!--        <div class="ot-slider-layer second" style="border-right: 3px solid #fff;">                    <a href="#" class="p-b">                        <span class="content-bottom">                            <span class="categories">                                <span class="category-tag vermelho-bg">esportes</span>                            </span>                            <h6>Ana Cláudia sofre lesão, deixa prova e lam